function moveElementos(){const prodDesc=document.getElementById("product-description"),prodFicha=document.getElementById("product-ficha"),prodRate=document.getElementById("product-rate");if(window.matchMedia("(max-width: 1199px)").matches){const prodDescMob=document.getElementById("mobile_product-description"),prodFichaMob=document.getElementById("mobile_product-ficha"),prodRateMob=document.getElementById("mobile_product-rate");prodDescMob.appendChild(prodDesc),prodFichaMob.appendChild(prodFicha),prodRateMob.appendChild(prodRate)}}window.addEventListener("load",moveElementos),window.addEventListener("resize",moveElementos);